from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Data
from langflow.schema.message import Message
import json
import re
import ast

class MinecraftDataFormatter(Component):
    display_name = "Minecraft Data Formatter"
    description = "Formats Minecraft observation data into human-readable text."
    documentation: str = "https://docs.langflow.org/components-custom-components"
    icon = "gamepad"
    name = "MinecraftDataFormatter"
    inputs = [
        MessageTextInput(
            name="minecraft_data",
            display_name="Minecraft Data",
            info="JSON format observation data from Minecraft",
            value="",
            required=True,
        ),
    ]
    outputs = [
        Output(display_name="Formatted Text", name="output", method="format_minecraft_data"),
    ]

    def format_minecraft_data(self) -> Message:
        try:
            raw_data = self.minecraft_data

            # 入力データの処理
            parsed_data = None
            if isinstance(raw_data, str):
                try:
                    parsed_data = ast.literal_eval(raw_data)
                except (SyntaxError, ValueError):
                    # 他の解析方法を試す
                    try:
                        parsed_data = json.loads(raw_data)
                    except json.JSONDecodeError:
                        parsed_data = raw_data
            else:
                parsed_data = raw_data

            # データ構造を解析
            observation_data = None
            if isinstance(parsed_data, list) and len(parsed_data) > 0:
                # ['observe', {...}] パターン
                if (len(parsed_data) >= 1 and isinstance(parsed_data[0], list) and 
                    len(parsed_data[0]) >= 2 and parsed_data[0][0] == 'observe'):
                    observation_data = parsed_data[0][1]

            # 解析されたデータがない場合のフォールバック
            if observation_data is None:
                error_message = "Could not extract Minecraft observation data from input."
                # ChatOutputに対応するために直接Messageオブジェクトを返す
                return Message(text=error_message)

            # 結果の構築
            formatted_text = ""

            # バイオーム情報
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'biome' in status:
                    biome = status['biome']
                    formatted_text += f"You are in a {biome} biome.\n"

            # 天気と時間
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict):
                    formatted_text += "The weather is clear."  # デフォルト

                    if 'timeOfDay' in status:
                        time_of_day = status['timeOfDay']
                        formatted_text += f"It is {time_of_day}."

            # エンティティ情報
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'entities' in status:
                    entities = status['entities']
                    if entities and isinstance(entities, dict):
                        for entity, distance in entities.items():
                            formatted_text += f"There is a {entity} {distance:.2f} blocks away."

            # セクション形式での情報整理
            formatted_text += "\nBiome: "
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'biome' in status:
                    biome = status['biome']
                    formatted_text += f"You are in a {biome} biome."

            formatted_text += "\nTime: "
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'timeOfDay' in status:
                    time_of_day = status['timeOfDay']
                    formatted_text += f"It is {time_of_day}."

            # ブロック情報
            formatted_text += "\nOther blocks: You can see these blocks nearby:"
            if isinstance(observation_data, dict) and 'voxels' in observation_data:
                blocks = observation_data['voxels']
                if isinstance(blocks, list):
                    for block in blocks:
                        formatted_text += f"\n- {block}"

            # 周辺エンティティ
            formatted_text += "\nNearby entities:"
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'entities' in status:
                    entities = status['entities']
                    if entities and isinstance(entities, dict) and len(entities) > 0:
                        for entity, distance in entities.items():
                            formatted_text += f"\n- {entity} (distance: {distance:.2f} blocks)"
                    else:
                        formatted_text += "\n- None"

            # 体力情報
            formatted_text += "\nHealth: "
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'health' in status:
                    health = status['health']
                    formatted_text += f"Your health is {health}/20."

            # 満腹度情報
            formatted_text += "\nHunger: "
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'food' in status and 'saturation' in status:
                    food = status['food']
                    saturation = status['saturation']
                    formatted_text += f"Your hunger is {food}/20 and saturation is {saturation}."

            # 位置情報
            formatted_text += "\nPosition: "
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'position' in status:
                    position = status['position']
                    if isinstance(position, dict):
                        x = position.get('x', 0)
                        y = position.get('y', 0)
                        z = position.get('z', 0)
                        formatted_text += f"You are at position (x={x}, y={y}, z={z})."

            # 地面との接触状態
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'onGround' in status:
                    on_ground = status['onGround']
                    if on_ground:
                        formatted_text += "\nYou are on the ground."
                    else:
                        formatted_text += "\nYou are not on the ground."

            # 水/溶岩の状態
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict):
                    is_in_water = status.get('isInWater', False)
                    is_in_lava = status.get('isInLava', False)
                    if not is_in_water and not is_in_lava:
                        formatted_text += "\nYou are not in water or lava."
                    elif is_in_water:
                        formatted_text += "\nYou are in water."
                    elif is_in_lava:
                        formatted_text += "\nYou are in lava."

            # 装備情報
            formatted_text += "\nEquipment: "
            if isinstance(observation_data, dict) and 'status' in observation_data:
                status = observation_data['status']
                if isinstance(status, dict) and 'equipment' in status:
                    equipment = status['equipment']
                    if isinstance(equipment, list):
                        if all(item is None for item in equipment):
                            formatted_text += "You have no equipment."
                        else:
                            formatted_text += "You have the following equipment:"
                            for i, item in enumerate(equipment):
                                if item is not None:
                                    formatted_text += f"\n- Slot {i}: {item}"

            # チェスト情報
            formatted_text += "\nChests: "
            if isinstance(observation_data, dict) and 'nearbyChests' in observation_data:
                chests = observation_data['nearbyChests']
                if chests and isinstance(chests, dict) and len(chests) > 0:
                    formatted_text += "Nearby chests contain:"
                    for chest_pos, contents in chests.items():
                        formatted_text += f"\n- Chest at {chest_pos}: {contents}"
                else:
                    formatted_text += "None"
            else:
                formatted_text += "None"

            # インベントリ情報
            formatted_text += "\nInventory: "
            if isinstance(observation_data, dict) and 'inventory' in observation_data:
                inventory = observation_data['inventory']
                if inventory and isinstance(inventory, dict) and len(inventory) > 0:
                    formatted_text += "You have the following items:"
                    for item, count in inventory.items():
                        formatted_text += f"\n- {item}: {count}"
                else:
                    formatted_text += "Your inventory is empty."
            else:
                formatted_text += "No inventory information available."

            # 重要な変更: Data オブジェクトではなく Message オブジェクトを返す
            return Message(text=formatted_text)

        except Exception as e:
            error_message = f"Error formatting Minecraft data: {str(e)}"
            # エラーの場合も Message オブジェクトを返す
            return Message(text=error_message)

