from enum import Enum
from typing import cast

from loguru import logger

from langflow.custom import Component
from langflow.io import DataInput, DropdownInput, MessageTextInput, Output
from langflow.schema import DataFrame


class DataOperation(str, Enum):
    CONCATENATE = "Concatenate"
    APPEND = "Append"
    MERGE = "Merge"
    JOIN = "Join"


class MergeDataComponent(Component):
    display_name = "Combine Data"
    description = "Combines data using different operations with customizable separator"
    icon = "merge"
    MIN_INPUTS_REQUIRED = 2

    inputs = [
        DataInput(name="data_inputs", display_name="Data Inputs", info="Data to combine", is_list=True, required=True),
        DropdownInput(
            name="operation",
            display_name="Operation Type",
            options=[op.value for op in DataOperation],
            value=DataOperation.CONCATENATE.value,
        ),
        MessageTextInput(
            name="separator",
            display_name="Text Separator",
            info="Separator used when combining text values (default: newline)",
            value="\n",
            advanced=True,
        ),
    ]
    outputs = [Output(display_name="DataFrame", name="combined_data", method="combine_data")]

    def combine_data(self) -> DataFrame:
        if not self.data_inputs or len(self.data_inputs) < self.MIN_INPUTS_REQUIRED:
            empty_dataframe = DataFrame()
            self.status = empty_dataframe
            return empty_dataframe

        operation = DataOperation(self.operation)

        # Get the separator with fallback to newline
        separator = self.separator if hasattr(self, 'separator') and self.separator else "\n"

        try:
            combined_dataframe = self._process_operation(operation, separator)
            self.status = combined_dataframe
        except Exception as e:
            logger.error(f"Error during operation {operation}: {e!s}")
            raise
        else:
            return combined_dataframe

    def _process_operation(self, operation: DataOperation, separator: str) -> DataFrame:
        if operation == DataOperation.CONCATENATE:
            combined_data: dict[str, str | object] = {}
            for data_input in self.data_inputs:
                for key, value in data_input.data.items():
                    if key in combined_data:
                        if isinstance(combined_data[key], str) and isinstance(value, str):
                            combined_data[key] = f"{combined_data[key]}{separator}{value}"
                        else:
                            combined_data[key] = value
                    else:
                        combined_data[key] = value
            return DataFrame([combined_data])

        if operation == DataOperation.APPEND:
            rows = [data_input.data for data_input in self.data_inputs]
            return DataFrame(rows)

        if operation == DataOperation.MERGE:
            result_data: dict[str, str | list[str] | object] = {}
            for data_input in self.data_inputs:
                for key, value in data_input.data.items():
                    if key in result_data and isinstance(value, str):
                        if isinstance(result_data[key], list):
                            cast("list[str]", result_data[key]).append(value)
                        else:
                            result_data[key] = [result_data[key], value]
                    else:
                        result_data[key] = value
            return DataFrame([result_data])

        if operation == DataOperation.JOIN:
            combined_data = {}
            for idx, data_input in enumerate(self.data_inputs, 1):
                for key, value in data_input.data.items():
                    new_key = f"{key}_doc{idx}" if idx > 1 else key
                    combined_data[new_key] = value
            return DataFrame([combined_data])

        return DataFrame()

from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema.message import Message
import re

class MinecraftCodeExtractor(Component):
    display_name = "Minecraft Code Extractor"
    description = "Extracts and parses JavaScript code from AI messages for Minecraft agent."
    documentation: str = "Extracts JavaScript code from AI messages, parses it, and returns program code, program name, and execution code separately."
    icon = "code"
    name = "MinecraftCodeExtractor"

    # クラス変数として初期化
    parsed_data = None
    error = False
    error_message = ""

    inputs = [
        MessageTextInput(
            name="ai_message",
            display_name="AI Message",
            info="AI message containing JavaScript code blocks",
            required=True,
        ),
    ]

    outputs = [
        Output(display_name="Program Code", name="program_code", method="extract_program_code"),
        Output(display_name="Program Name", name="program_name", method="extract_program_name"),
        Output(display_name="Execution Code", name="exec_code", method="extract_exec_code"),
        Output(display_name="Error Status", name="error_status", method="get_error_status"),
    ]

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        # インスタンス変数として初期化
        self.parsed_data = None
        self.error = False
        self.error_message = ""

    def parse_js_functions(self, code):
        """Simple JavaScript function parser without using Babel."""
        functions = []

        # Simple regex to find function declarations
        # This is a simplified approach - a real implementation would use a proper JS parser
        async_function_pattern = re.compile(r"async\s+function\s+([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\(([^)]*)\)\s*{", re.DOTALL)
        function_pattern = re.compile(r"function\s+([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\(([^)]*)\)\s*{", re.DOTALL)

        # Find async functions
        for match in async_function_pattern.finditer(code):
            name = match.group(1)
            params = [param.strip() for param in match.group(2).split(',') if param.strip()]
            start_pos = match.start()

            # Find the end of the function by matching braces
            body, end_pos = self.extract_function_body(code, start_pos)

            functions.append({
                "name": name,
                "is_async": True,
                "params": params,
                "body": body,
                "declaration": match.group(0),
                "full_function": code[start_pos:end_pos]
            })

        # Find regular functions
        for match in function_pattern.finditer(code):
            # Skip if this function was already captured as async
            if any(f["name"] == match.group(1) and f["is_async"] for f in functions):
                continue

            name = match.group(1)
            params = [param.strip() for param in match.group(2).split(',') if param.strip()]
            start_pos = match.start()

            # Find the end of the function by matching braces
            body, end_pos = self.extract_function_body(code, start_pos)

            functions.append({
                "name": name,
                "is_async": False,
                "params": params,
                "body": body,
                "declaration": match.group(0),
                "full_function": code[start_pos:end_pos]
            })

        return functions

    def extract_function_body(self, code, start_pos):
        """Extract function body by matching braces."""
        brace_count = 0
        in_string = False
        string_char = None
        escaped = False

        # Find the opening brace
        i = start_pos
        while i < len(code):
            if code[i] == '{' and not in_string:
                brace_count = 1
                body_start = i
                break
            i += 1

        if brace_count == 0:
            return "", start_pos

        # Find the matching closing brace
        i = body_start + 1
        while i < len(code) and brace_count > 0:
            char = code[i]

            if not in_string:
                if char == '{':
                    brace_count += 1
                elif char == '}':
                    brace_count -= 1
                elif char in ['"', "'"]:
                    in_string = True
                    string_char = char
            else:
                if char == '\\':
                    escaped = not escaped
                elif char == string_char and not escaped:
                    in_string = False
                else:
                    escaped = False

            i += 1

        if brace_count == 0:
            body_end = i
            return code[body_start:body_end], body_end
        else:
            return code[body_start:], len(code)

    def validate_main_function_params(self, function):
        """Validate that the main function takes a single 'bot' parameter."""
        return len(function["params"]) == 1 and function["params"][0] == "bot"

    def process_ai_message(self):
        """Process AI message to extract JavaScript code."""
        try:
            # Reset state
            self.error = False
            self.error_message = ""

            # Get the message content
            message_content = ""
            if hasattr(self.ai_message, 'content'):
                message_content = self.ai_message.content
            elif hasattr(self.ai_message, 'get_text') and callable(getattr(self.ai_message, 'get_text')):
                message_content = self.ai_message.get_text()
            elif isinstance(self.ai_message, str):
                message_content = self.ai_message
            else:
                self.error = True
                self.error_message = "Invalid message format. Expected Message object or string."
                return None

            # Extract JavaScript code blocks
            code_pattern = re.compile(r"```(?:javascript|js)(.*?)```", re.DOTALL)
            code_blocks = code_pattern.findall(message_content)

            if not code_blocks:
                self.error = True
                self.error_message = "No JavaScript code blocks found in the message."
                return None

            code = "\n".join(code_blocks)

            # Parse the code using a simplified approach since we can't use Babel directly
            functions = self.parse_js_functions(code)

            if not functions:
                self.error = True
                self.error_message = "No functions found in the extracted code."
                return None

            # Find the main async function
            main_function = None
            for function in reversed(functions):
                if function["is_async"]:
                    main_function = function
                    break

            if main_function is None:
                self.error = True
                self.error_message = "No async function found. Your main function must be async."
                return None

            # Validate main function parameters
            if not self.validate_main_function_params(main_function):
                self.error = True
                self.error_message = f"Main function {main_function['name']} must take a single argument named 'bot'"
                return None

            # Prepare the output
            # Instead of just returning the function bodies, return complete functions with declarations
            program_code = "\n\n".join(function["full_function"] for function in functions)
            program_name = main_function["name"]
            exec_code = f"await {program_name}(bot);"

            # Store the parsed data for outputs
            self.parsed_data = {
                "program_code": program_code,
                "program_name": program_name,
                "exec_code": exec_code
            }

            return self.parsed_data

        except Exception as e:
            self.error = True
            self.error_message = f"Error parsing action response: {str(e)}"
            return None

    def extract_program_code(self) -> Message:
        """Extract and return the program code as a Message."""
        # Process the AI message if it hasn't been processed yet
        if not hasattr(self, 'parsed_data') or self.parsed_data is None:
            self.process_ai_message()

        if self.error:
            return Message(text=f"Error: {self.error_message}")

        if self.parsed_data and "program_code" in self.parsed_data:
            return Message(text=self.parsed_data["program_code"])
        else:
            return Message(text="Error: Failed to extract program code")

    def extract_program_name(self) -> Message:
        """Extract and return the program name as a Message."""
        # Process the AI message if it hasn't been processed yet
        if not hasattr(self, 'parsed_data') or self.parsed_data is None:
            self.process_ai_message()

        if self.error:
            return Message(text=f"Error: {self.error_message}")

        if self.parsed_data and "program_name" in self.parsed_data:
            return Message(text=self.parsed_data["program_name"])
        else:
            return Message(text="Error: Failed to extract program name")

    def extract_exec_code(self) -> Message:
        """Extract and return the execution code as a Message."""
        # Process the AI message if it hasn't been processed yet
        if not hasattr(self, 'parsed_data') or self.parsed_data is None:
            self.process_ai_message()

        if self.error:
            return Message(text=f"Error: {self.error_message}")

        if self.parsed_data and "exec_code" in self.parsed_data:
            return Message(text=self.parsed_data["exec_code"])
        else:
            return Message(text="Error: Failed to extract execution code")

    def get_error_status(self) -> Message:
        """Return the error status as a Message."""
        # Process the AI message if it hasn't been processed yet
        if not hasattr(self, 'parsed_data') or self.parsed_data is None:
            self.process_ai_message()

        return Message(text=str(self.error))
